import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import * as API from '../services/api';
import TransactionList from '../components/TransactionList';

function HomePage() {
  const navigate = useNavigate();
  const [transactions, setTransactions] = useState([]);
  const [defaultUser, setDefaultUser] = useState(null);

  // Load transactions
  const loadTransactions = () => {
    if (!defaultUser) return;
    API.getTransactions()
      .then(data => setTransactions(data || []))
      .catch(err => {
        console.error(err);
        setTransactions([]);
      });
  };

  // Load default user and transactions on mount
  useEffect(() => {
    API.getDefaultUser()
      .then(user => {
        setDefaultUser(user);
        loadTransactions();
      })
      .catch(err => {
        console.error(err);
        setTransactions([]);
      });
  }, []);

  return (
    <div style={{ padding: 20 }}>
      <h1>Welcome to Budget Tracker</h1>

      {/* Original buttons */}
      <button onClick={() => navigate('/edit')}>Edit Transactions</button>
      <button onClick={() => navigate('/summary')} style={{ marginLeft: 10 }}>View Summary</button>

      {/* Any other existing homepage content stays here */}

      {/* Transaction history UI at the bottom */}
      <h2 style={{ marginTop: 40 }}>Transaction History</h2>
      {transactions.length === 0 ? (
        <p>No transactions yet.</p>
      ) : (
        <TransactionList transactions={transactions} remove={() => {}} />
      )}
    </div>
  );
}

export default HomePage;
