import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import * as API from '../services/api';

function EditPage() {
  const navigate = useNavigate();
  const [transactions, setTransactions] = useState([]);
  const [form, setForm] = useState({ description: '', amount: '', category: '' });
  const [error, setError] = useState('');
  const [defaultUser, setDefaultUser] = useState(null);

  const load = () => {
    API.getTransactions()
      .then((res) => setTransactions(res))
      .catch(() => setTransactions([]));
  };

  useEffect(() => {
    API.getDefaultUser().then(user => {
      setDefaultUser(user); // store the user for adding transactions
      load();               // load transactions
    });
  }, []);

  const submit = () => {
    if (!defaultUser) return; // safety check

    const payload = {
      description: form.description,
      amount: parseFloat(form.amount),
      category: form.category,
      type: parseFloat(form.amount) >= 0 ? 'income' : 'expense',
      user: defaultUser._id
    };

    API.addtransaction(payload)
      .then(() => {
        load();
        setForm({ description: '', amount: '', category: '' });
        setError('');
      })
      .catch((err) => {
        console.error(err.response?.data || err);
        setError(err.response?.data?.error || 'Error adding transaction');
      });
  };

  const remove = (id) => {
    API.deleteTransaction(id)
      .then(() => load())
      .catch((err) => console.error(err.response?.data || err));
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Edit Transactions</h1>
      <button onClick={() => navigate('/')}>Back</button>

      <h3>Add Transaction</h3>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      <input
        placeholder="Description"
        value={form.description}
        onChange={(e) => setForm({ ...form, description: e.target.value })}
      />
      <input
        placeholder="Amount"
        type="number"
        value={form.amount}
        onChange={(e) => setForm({ ...form, amount: e.target.value })}
      />
      <input
        placeholder="Category"
        value={form.category}
        onChange={(e) => setForm({ ...form, category: e.target.value })}
      />
      <button onClick={submit}>Add</button>

      <h3 style={{ marginTop: 20 }}>Existing Transactions</h3>
      {transactions.length === 0 && <div>No transactions found.</div>}
      {transactions.map((t) => (
        <div key={t._id} style={{ background: 'white', padding: 10, marginBottom: 6 }}>
          {t.description} â€” ${t.amount} ({t.type}) [{t.category}]
          <button style={{ marginLeft: 10 }} onClick={() => remove(t._id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

export default EditPage;
