import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import * as API from '../services/api';
import TransactionList from './TransactionList';

function EditPage() {
  const navigate = useNavigate();
  const [transactions, setTransactions] = useState([]);
  const [form, setForm] = useState({ description: '', amount: '', category: '' });
  const [error, setError] = useState('');
  const [defaultUser, setDefaultUser] = useState(null);

  // Load transactions for the given user
  const loadTransactions = (userId) => {
    API.getTransactions()
      .then(data => {
        setTransactions(data || []); // always set an array
      })
      .catch(err => {
        console.error(err);
        setTransactions([]);
      });
  };

  // On mount, get default user and load their transactions
  useEffect(() => {
    API.getDefaultUser()
      .then(user => {
        setDefaultUser(user);
        loadTransactions(user._id);
      })
      .catch(err => {
        console.error(err);
        setTransactions([]); // fallback so UI still renders
      });
  }, []);

  // Add a transaction
  const submit = () => {
    if (!defaultUser) return;

    const payload = {
      description: form.description,
      amount: parseFloat(form.amount),
      category: form.category,
      type: parseFloat(form.amount) >= 0 ? 'income' : 'expense',
      user: defaultUser._id
    };

    API.addtransaction(payload)
      .then(() => {
        setForm({ description: '', amount: '', category: '' });
        setError('');
        loadTransactions(defaultUser._id); // refresh list
      })
      .catch(err => {
        console.error(err.response?.data || err);
        setError(err.response?.data?.error || 'Error adding transaction');
      });
  };

  // Delete a transaction
  const remove = (id) => {
    API.deleteTransaction(id)
      .then(() => loadTransactions(defaultUser._id))
      .catch(err => console.error(err.response?.data || err));
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Edit Transactions</h1>
      <button onClick={() => navigate('/')}>Back</button>

      <h3>Add Transaction</h3>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      <input
        placeholder="Description"
        value={form.description}
        onChange={(e) => setForm({ ...form, description: e.target.value })}
      />
      <input
        placeholder="Amount"
        type="number"
        value={form.amount}
        onChange={(e) => setForm({ ...form, amount: e.target.value })}
      />
      <input
        placeholder="Category"
        value={form.category}
        onChange={(e) => setForm({ ...form, category: e.target.value })}
      />
      <button onClick={submit}>Add</button>

      <h3 style={{ marginTop: 20 }}>Existing Transactions</h3>
      <TransactionList transactions={transactions} remove={remove} />
    </div>
  );
}

export default EditPage;
