import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import * as API from '../services/api';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register scales and elements
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function SummaryPage() {
  const navigate = useNavigate();
  const [transactions, setTransactions] = useState([]);
  const [defaultUser, setDefaultUser] = useState(null);

  useEffect(() => {
    // Load default user first
    API.getDefaultUser()
      .then(user => {
        setDefaultUser(user);
        return API.getTransactions(); // load transactions
      })
      .then(data => setTransactions(data || []))
      .catch(err => {
        console.error(err);
        setTransactions([]);
      });
  }, []);

  // Aggregate amounts per category
  const categoryTotals = transactions.reduce((acc, t) => {
    const category = t.category || 'Other';
    acc[category] = (acc[category] || 0) + t.amount;
    return acc;
  }, {});

  const labels = Object.keys(categoryTotals);
  const data = {
    labels,
    datasets: [
      {
        label: 'Amount',
        data: labels.map(label => categoryTotals[label]),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: { 
        position: 'top',
        labels: { color: 'white' } // legend text
      },
      title: { 
        display: false // removed the Summary Page header
      },
      tooltip: {
        titleColor: 'white',
        bodyColor: 'white',
        backgroundColor: 'rgba(0,0,0,0.7)'
      }
    },
    scales: {
      x: { 
        type: 'category',
        ticks: { color: 'white' },
        grid: { color: 'rgba(255,255,255,0.2)' }
      },
      y: { 
        type: 'linear',
        ticks: { color: 'white' },
        grid: { color: 'rgba(255,255,255,0.2)' }
      },
    },
  };

  return (
    <div style={{ padding: 20 }}>
      <button 
        style={{
          background: 'white',
          color: 'black',
          border: 'none',
          padding: '10px 18px',
          borderRadius: '8px',
          cursor: 'pointer',
          marginBottom: '20px'
        }}
        onClick={() => navigate('/')}
      >
        Back
      </button>

      {transactions.length === 0 ? (
        <p>Summary</p>
      ) : (
        <Bar data={data} options={options} />
      )}
    </div>
  );
}

export default SummaryPage;
