import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import * as API from '../services/api';
import TransactionList from '../components/TransactionList';

function EditPage() {
  const navigate = useNavigate();
  const [transactions, setTransactions] = useState([]);
  const [form, setForm] = useState({ description: '', amount: '', category: '' });
  const [error, setError] = useState('');
  const [defaultUser, setDefaultUser] = useState(null);

  // Load transactions
  const loadTransactions = () => {
    if (!defaultUser) return;
    API.getTransactions()
      .then((data) => setTransactions(data || []))
      .catch(() => setTransactions([]));
  };

  // Load default user and transactions on mount
  useEffect(() => {
    API.getDefaultUser()
      .then((user) => {
        setDefaultUser(user);
        loadTransactions();
      })
      .catch((err) => console.error(err));
  }, []);

  // Submit new transaction
  const submit = () => {
    if (!defaultUser) return;

    const payload = {
      description: form.description,
      amount: parseFloat(form.amount),
      category: form.category,
      type: parseFloat(form.amount) >= 0 ? 'income' : 'expense',
      user: defaultUser._id,
    };

    API.addtransaction(payload)
      .then(() => {
        setForm({ description: '', amount: '', category: '' });
        setError('');
        loadTransactions();
      })
      .catch((err) => {
        console.error(err.response?.data || err);
        setError(err.response?.data?.error || 'Error adding transaction');
      });
  };

  // Delete transaction
  const remove = (id) => {
    API.deleteTransaction(id)
      .then(() => loadTransactions())
      .catch((err) => console.error(err.response?.data || err));
  };

  return (
    <div style={{ padding: 20 }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <button onClick={() => navigate('/')}>Back</button>
        <h1 style={{ textAlign: 'center', flexGrow: 1 }}>Edit Transactions</h1>
      </div>

      {/* Add Transaction Form */}
      <div style={{ marginTop: 40, marginBottom: 60, textAlign: 'center' }}>
        <h3>Add Transaction</h3>
        {error && <div style={{ color: 'red', marginBottom: 8 }}>{error}</div>}
        <input
          placeholder="Description"
          value={form.description}
          onChange={(e) => setForm({ ...form, description: e.target.value })}
          style={{ display: 'block', margin: '8px auto' }}
        />
        <input
          placeholder="Amount"
          type="number"
          value={form.amount}
          onChange={(e) => setForm({ ...form, amount: e.target.value })}
          style={{ display: 'block', margin: '8px auto' }}
        />
        <input
          placeholder="Category"
          value={form.category}
          onChange={(e) => setForm({ ...form, category: e.target.value })}
          style={{ display: 'block', margin: '8px auto' }}
        />
        <button onClick={submit} style={{ marginTop: 12 }}>
          Add
        </button>
      </div>

      <h3 style={{ marginBottom: 12 }}>Existing Transactions</h3>
      {transactions.length === 0 ? (
        <div>No transactions found.</div>
      ) : (
        <TransactionList transactions={transactions} remove={remove} />
      )}
    </div>
  );
}

export default EditPage;
