import React from 'react';
import './transactions.css'; // make sure styling still applies

const TransactionList = ({ transactions = [], remove, showDelete = false }) => {
  if (!Array.isArray(transactions)) {
    console.error("TransactionList received non-array:", transactions);
    return <p>Error loading transactions.</p>;
  }

  return (
    <div className="transaction-list">
      {transactions.length === 0 ? (
        <p>No transactions yet.</p>
      ) : (
        transactions.map((t) => (
          <div key={t._id} className="transaction-item">
            <div>
              <strong>{t.description}</strong> â€” ${t.amount.toFixed(2)} ({t.type}) [{t.category}]
            </div>
            {showDelete && (
              <button style={{ marginLeft: 10 }} onClick={() => remove && remove(t._id)}>
                Delete
              </button>
            )}
          </div>
        ))
      )}
    </div>
  );
};

export default TransactionList;
