import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import * as API from '../services/api';
import TransactionList from '../components/TransactionList';

function EditPage() {
  const navigate = useNavigate();
  const [transactions, setTransactions] = useState([]);
  const [form, setForm] = useState({ description: '', amount: '', category: '' });
  const [error, setError] = useState('');
  const [defaultUser, setDefaultUser] = useState(null);

  // Load transactions for the given user
  const loadTransactions = () => {
    if (!defaultUser) return;
    API.getTransactions()
      .then((data) => setTransactions(data || []))
      .catch(() => setTransactions([]));
  };

  // On mount, get default user and load their transactions
  useEffect(() => {
    API.getDefaultUser()
      .then((user) => {
        setDefaultUser(user);
        loadTransactions();
      })
      .catch((err) => {
        console.error(err);
        setTransactions([]);
      });
  }, []);

  // Add a transaction
  const submit = () => {
    if (!defaultUser) return;

    const payload = {
      description: form.description,
      amount: parseFloat(form.amount),
      category: form.category,
      type: parseFloat(form.amount) >= 0 ? 'income' : 'expense',
      user: defaultUser._id
    };

    API.addtransaction(payload)
      .then(() => {
        setForm({ description: '', amount: '', category: '' });
        setError('');
        loadTransactions();
      })
      .catch((err) => {
        console.error(err.response?.data || err);
        setError(err.response?.data?.error || 'Error adding transaction');
      });
  };

  // Delete a transaction
  const remove = (id) => {
    API.deleteTransaction(id)
      .then(() => loadTransactions())
      .catch((err) => console.error(err.response?.data || err));
  };

  return (
    <div style={{ padding: 20 }}>
      {/* Header and Back button */}
      <div style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        marginBottom: 20
      }}>
        <button onClick={() => navigate('/')} style={{ marginRight: 'auto' }}>Back</button>
        <h1 style={{ textAlign: 'center', flexGrow: 1 }}>Edit Transactions</h1>
        <div style={{ width: 75 }} /> {/* empty div to balance spacing */}
      </div>

      {/* Add Transaction Form */}
      <div style={{ marginBottom: 40 }}>
        <h3>Add Transaction</h3>
        {error && <div style={{ color: 'red' }}>{error}</div>}
        <input
          placeholder="Description"
          value={form.description}
          onChange={(e) => setForm({ ...form, description: e.target.value })}
        />
        <input
          placeholder="Amount"
          type="number"
          value={form.amount}
          onChange={(e) => setForm({ ...form, amount: e.target.value })}
        />
        <input
          placeholder="Category"
          value={form.category}
          onChange={(e) => setForm({ ...form, category: e.target.value })}
        />
        <button onClick={submit}>Add</button>
      </div>

      {/* Existing Transactions */}
      <h3>Existing Transactions</h3>
      <TransactionList transactions={transactions} remove={remove} />
    </div>
  );
}

export default EditPage;
