import React, { useEffect, useState } from 'react';
import * as API from '../services/api';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register scales and elements
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function SummaryPage() {
  const [transactions, setTransactions] = useState([]);
  const [defaultUser, setDefaultUser] = useState(null);

  useEffect(() => {
    // Load default user first
    API.getDefaultUser()
      .then(user => {
        setDefaultUser(user);
        return API.getTransactions(); // load transactions
      })
      .then(data => setTransactions(data || []))
      .catch(err => {
        console.error(err);
        setTransactions([]);
      });
  }, []);

  // Aggregate amounts per category
  const categoryTotals = transactions.reduce((acc, t) => {
    const category = t.category || 'Other';
    acc[category] = (acc[category] || 0) + t.amount;
    return acc;
  }, {});

  const labels = Object.keys(categoryTotals);
  const data = {
    labels,
    datasets: [
      {
        label: 'Amount',
        data: labels.map(label => categoryTotals[label]),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: { position: 'top' },
      title: { display: true, text: 'Transaction Summary by Category' },
    },
    scales: {
      x: { type: 'category' },
      y: { type: 'linear' },
    },
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Summary Page</h1>
      {transactions.length === 0 ? (
        <p>No transactions available.</p>
      ) : (
        <Bar data={data} options={options} />
      )}
    </div>
  );
}

export default SummaryPage;
