import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import * as API from '../services/api';
import TransactionList from '../components/TransactionList';

function HomePage() {
  const navigate = useNavigate();
  const [transactions, setTransactions] = useState([]);
  const [defaultUser, setDefaultUser] = useState(null);

  const loadTransactions = () => {
    if (!defaultUser) return;
    API.getTransactions()
      .then((data) => setTransactions(data || []))
      .catch(() => setTransactions([]));
  };

  useEffect(() => {
    API.getDefaultUser()
      .then((user) => {
        setDefaultUser(user);
        loadTransactions();
      })
      .catch((err) => console.error(err));
  }, []);

  return (
    <div style={{ padding: 20, textAlign: 'center' }}>
      <h1>Home Page</h1>
      <button onClick={() => navigate('/edit')} style={{ margin: 5 }}>Edit Transactions</button>
      <button onClick={() => navigate('/summary')} style={{ margin: 5 }}>Summary</button>

      <h3 style={{ textAlign: 'left', marginTop: 30 }}>Transaction History</h3>
      <TransactionList transactions={transactions} showDelete={false} />
    </div>
  );
}

export default HomePage;
