import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import * as API from '../services/api';
import TransactionList from '../components/TransactionList';

function EditPage() {
  const navigate = useNavigate();
  const [transactions, setTransactions] = useState([]);
  const [form, setForm] = useState({ description: '', amount: '', category: '' });
  const [error, setError] = useState('');
  const [defaultUser, setDefaultUser] = useState(null);

  const loadTransactions = () => {
    if (!defaultUser) return;
    API.getTransactions()
      .then((data) => setTransactions(data || []))
      .catch(() => setTransactions([]));
  };

  useEffect(() => {
    API.getDefaultUser()
      .then((user) => {
        setDefaultUser(user);
        loadTransactions();
      })
      .catch((err) => console.error(err));
  }, []);

  const submit = () => {
    if (!defaultUser) return;

    const payload = {
      description: form.description,
      amount: parseFloat(form.amount),
      category: form.category,
      type: parseFloat(form.amount) >= 0 ? 'income' : 'expense',
      user: defaultUser._id
    };

    API.addtransaction(payload)
      .then(() => {
        setForm({ description: '', amount: '', category: '' });
        setError('');
        loadTransactions();
      })
      .catch((err) => {
        console.error(err.response?.data || err);
        setError(err.response?.data?.error || 'Error adding transaction');
      });
  };

  const remove = (id) => {
    API.deleteTransaction(id)
      .then(() => loadTransactions())
      .catch((err) => console.error(err.response?.data || err));
  };

  return (
    <div style={{ padding: 20 }}>
      <h1 style={{ textAlign: 'center' }}>Edit Transactions</h1>
      <button onClick={() => navigate('/')} style={{ marginBottom: 20 }}>Back</button>

      <h3 style={{ textAlign: 'center' }}>Add Transaction</h3>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      <div style={{ display: 'flex', justifyContent: 'center', gap: '10px', marginBottom: '20px' }}>
        <input
          placeholder="Description"
          value={form.description}
          onChange={(e) => setForm({ ...form, description: e.target.value })}
        />
        <input
          placeholder="Amount"
          type="number"
          value={form.amount}
          onChange={(e) => setForm({ ...form, amount: e.target.value })}
        />
        <input
          placeholder="Category"
          value={form.category}
          onChange={(e) => setForm({ ...form, category: e.target.value })}
        />
        <button onClick={submit}>Add</button>
      </div>

      <h3>Existing Transactions</h3>
      <TransactionList transactions={transactions} remove={remove} showDelete={true} />
    </div>
  );
}

export default EditPage;
