import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import * as API from '../services/api';
import TransactionList from '../components/TransactionList';

function HomePage() {
  const navigate = useNavigate();
  const [transactions, setTransactions] = useState([]);
  const [defaultUser, setDefaultUser] = useState(null);

  // Load default user and transactions
  useEffect(() => {
    API.getDefaultUser()
      .then(user => {
        setDefaultUser(user);
        API.getTransactions() // fetch all transactions or add filtering if needed
          .then(data => setTransactions(data || []))
          .catch(err => {
            console.error(err);
            setTransactions([]);
          });
      })
      .catch(err => {
        console.error(err);
        setTransactions([]);
      });
  }, []);

  return (
    <div style={{ padding: 20 }}>
      <h1>Budget Tracker</h1>

      <div style={{ marginBottom: 20 }}>
        <button onClick={() => navigate('/edit')} style={{ marginRight: 10 }}>
          Edit Transactions
        </button>
        <button onClick={() => navigate('/summary')}>
          View Summary
        </button>
      </div>

      <h2>Transaction History</h2>
      {transactions.length === 0 && <div>No transaction history.</div>}
      <TransactionList transactions={transactions} />
    </div>
  );
}

export default HomePage;
